import 'package:flutter/material.dart';

class FourthScreen extends StatelessWidget {
  const FourthScreen({super.key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
          leading: IconButton(
            icon: Icon(Icons.arrow_back, color: Colors.black),
            onPressed: () => Navigator.of(context).pop(),
          ),
          title: Text("EV URUNLERI",
              style: TextStyle(
                  color: Colors.black,
                  fontWeight: FontWeight.w600,
                  fontSize: 36)),
          centerTitle: true,
          backgroundColor: const Color.fromARGB(255, 28, 95, 30)),
      body: Container(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            Row(
              mainAxisAlignment: MainAxisAlignment.spaceEvenly,
              children: [
                Column(
                  mainAxisAlignment: MainAxisAlignment.center,
                  crossAxisAlignment: CrossAxisAlignment.center,
                  children: [
                    Image.network(
                      "data:image/jpeg;base64,",
                      height: 150,
                      width: 150,
                    ),
                    const SizedBox(
                      height: 20,
                    ),
                    GestureDetector(
                      onTap: () {
                        _showDialog(context, 'CAM PIPET',
                            'Pipetler geri dönüştürülemediği için ve tek kullanımlık olduğu için, büyük depolama alanlarında biriktiriliyor ve ardından okyanuslara atılıyor. Okyanuslara atılan pipetler ise su kirliliğine neden oluyor ve suda yaşayan canlıların hayatını tehlikeye atıyor.Bu yuzden cam pipetler kullanilmalidir. ');
                      },
                      child: Text(
                        'CAM PIPET',
                        style: TextStyle(
                            fontWeight: FontWeight.bold,
                            color: Colors.black,
                            fontSize: 24),
                      ),
                    ),
                  ],
                ),
                Column(
                  mainAxisAlignment: MainAxisAlignment.center,
                  crossAxisAlignment: CrossAxisAlignment.center,
                  children: [
                    Image.network(
                      "data:image/jpeg;base64,",
                      height: 150,
                      width: 150,
                    ),
                    const SizedBox(
                      height: 20,
                    ),
                    GestureDetector(
                      onTap: () {
                        _showDialog(context, 'BALMUMU KUMASLAR',
                            'Strec filmler gibi tek kullanimlik gida saklama yontemlerine birer alternatiftir.Yikanabilir olmasindan dolayi  uzun omurludur. ');
                      },
                      child: Text(
                        'BALMUMU KUMASLAR',
                        style: TextStyle(
                            fontWeight: FontWeight.bold,
                            color: Colors.black,
                            fontSize: 24),
                      ),
                    ),
                  ],
                ),
                Column(
                  mainAxisAlignment: MainAxisAlignment.center,
                  crossAxisAlignment: CrossAxisAlignment.center,
                  children: [
                    Image.network(
                      "data:image/jpeg;base64,",
                      height: 150,
                      width: 150,
                    ),
                    const SizedBox(
                      height: 20,
                    ),
                    GestureDetector(
                      onTap: () {
                        _showDialog(context, 'KESE KAGITLARI',
                            'Tek kullanimlilik posetler digada cozunememektedir.Kese kagitlarinin geri donusumu mumkundur.');
                      },
                      child: Text(
                        'KESE KAGITLARI',
                        style: TextStyle(
                            fontWeight: FontWeight.bold,
                            color: Colors.black,
                            fontSize: 24),
                      ),
                    ),
                  ],
                ),
              ],
            ),
            // ...
          ],
        ),
      ),
    );
  }

  void _showDialog(BuildContext context, String title, String description) {
    showDialog(
      context: context,
      builder: (BuildContext context) {
        return AlertDialog(
          title: Text(title),
          content: Text(description),
          actions: [
            ElevatedButton(
              child: Text("OK"),
              onPressed: () {
                Navigator.of(context).pop();
              },
            ),
          ],
        );
      },
    );
  }
}
